import React from "react";
import CardContainer from "./CardsContainer";
import { useState, useEffect } from "react";

const Body = () => {
  const [future, setFuture] = useState(0);
  const [distance, setDistance] = useState(0);
  const [times, setTimes] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0,
  });

  useEffect(() => {
    if (future) {
      setInterval(() => {
        const now = new Date().getTime();
        setDistance(future - now);
        setTimes({
          days: Math.floor(distance / (1000 * 60 * 60 * 24)),
          hours: Math.floor(
            (distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
          ),
          minutes: Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60)),
          seconds: Math.floor((distance % (1000 * 60)) / 1000),
        });
      }, 1000);
    }
  }, [distance]);

  const handleDateInput = (e) => {
    e.preventDefault();
    const hoursInMill = e.target.hours.value * 1000 * 60 * 60;
    const minutesInMill = e.target.minutes.value * 1000 * 60;
    const secondsInMill = e.target.seconds.value * 1000;

    const sumOfTime = hoursInMill + minutesInMill + secondsInMill;
    const presentTime = new Date().getTime() + sumOfTime;

    setFuture(presentTime);
  };

  // const hours = Math.floor(
  //   (distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
  // );
  // const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
  // const seconds = Math.floor((distance % (1000 * 60)) / 1000);
  // const days = Math.floor(distance / (1000 * 60 * 60 * 24));

  return (
    <div className="container">
      <form className="get-time" onSubmit={(e) => handleDateInput(e)}>
        <input type="number" name="hours" id="hours" placeholder="Hours" />
        <input
          type="number"
          name="minutes"
          id="minutes"
          placeholder="Minutes"
        />
        <input
          type="number"
          name="seconds"
          id="seconds"
          placeholder="Seconds"
        />
        <input type="submit" value="Start" />
      </form>

      <CardContainer
        days={times.days}
        hours={times.hours}
        minutes={times.minutes}
        seconds={times.seconds}
      />
    </div>
  );
};

export default Body;
