import React from "react";
import CardContainer from "./CardsContainer";
import { useState, useEffect } from "react";

const Body = () => {
  const [future, setFuture] = useState(0);
  const [distance, setDistance] = useState(0);

  useEffect(() => {
    if (future) {
      setInterval(() => {
        const now = new Date().getTime();
        setDistance(future - now);
      }, 1000);
    }
  }, [future]);

  const handleDateInput = (e) => {
    setFuture(0)
    setDistance(0)
    const input = e.target.value;
    setFuture(new Date(input).getTime());
  };

  const hours = Math.floor(
    (distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
  );
  const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
  const seconds = Math.floor((distance % (1000 * 60)) / 1000);
  const days = Math.floor(distance / (1000 * 60 * 60 * 24));

  return (
    <div className="container">
      <form className="get-time">
       <input type="number" name="hours" id="hours" placeholder="Hours" />
       <input type="number" name="minutes" id="minutes" placeholder="Minutes"/>
       <input type="number" name="seconds" id="seconds" placeholder="Seconds" />
       <input type="submit" value="Start" onSU={ () => handleDateInput()} />
      </form>
      
      
      
        <CardContainer
        days={days}
        hours={hours}
        minutes={minutes}
        seconds={seconds}
      />
    </div>
  );
};

export default Body;
